--
-- Name: v_eigentuemer; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.v_eigentuemer AS
 SELECT f.ogc_fid,
    f.gml_id,
    f.wkb_geometry,
    fs.flsnr,
    fs.amtlflsfl,
    ( SELECT gema_shl.gemarkung
           FROM public.gema_shl
          WHERE (gema_shl.gemashl = fs.gemashl)) AS gemarkung,
    ( SELECT array_to_string((array_agg(DISTINCT ((str_shl.strname)::text || COALESCE((' '::text || (strassen.hausnr)::text), ''::text))) ||
                CASE
                    WHEN (fs.lagebez IS NOT NULL) THEN (ARRAY[fs.lagebez])::text[]
                    ELSE '{}'::text[]
                END), '
'::text) AS array_to_string
           FROM (public.strassen
             LEFT JOIN public.str_shl ON ((strassen.strshl = str_shl.strshl)))
          WHERE ((strassen.flsnr = (fs.flsnr)::bpchar) AND (strassen.ff_stand = 0))) AS adressen,
    ( SELECT array_to_string(array_agg(DISTINCT ea.bestdnr), '
'::text) AS array_to_string
           FROM public.eignerart ea
          WHERE ((ea.flsnr = (fs.flsnr)::bpchar) AND (ea.ff_stand = 0))) AS bestaende,
    ( SELECT array_to_string(array_agg(DISTINCT ((((e.name1)::text || COALESCE((', '::text || (e.name2)::text), ''::text)) || COALESCE((', '::text || (e.name3)::text), ''::text)) || COALESCE((', '::text || (e.name4)::text), ''::text))), '
'::text) AS array_to_string
           FROM (public.eignerart ea
             JOIN public.eigner e ON (((ea.bestdnr = e.bestdnr) AND (e.ff_stand = 0))))
          WHERE ((ea.flsnr = (fs.flsnr)::bpchar) AND (ea.ff_stand = 0))) AS eigentuemer
   FROM (public.ax_flurstueck f
     JOIN public.flurst fs ON (((fs.ff_stand = 0) AND ((public.alkis_flsnr(f.*))::text = (fs.flsnr)::text))))
  WHERE (f.endet IS NULL)
  GROUP BY f.ogc_fid, f.gml_id, f.wkb_geometry, fs.flsnr, fs.gemashl, fs.lagebez, fs.amtlflsfl;


ALTER TABLE public.v_eigentuemer OWNER TO postgres;

