            public.st_centroid(g.wkb_geometry) AS point,
            (((((to_char(public.alkis_toint(o.land), 'fm00'::text) || (o.regierungsbezirk)::text) || to_char(public.alkis_toint(o.kreis), 'fm00'::text)) || to_char(public.alkis_toint(o.gemeinde), 'fm000'::text)) || '    '::text) || TRIM(BOTH FROM o.lage)) AS strshl,
