// Sammle ID für NominalTrackGauge
if (nominalTrackGauge) {
	collect.nominalTrackGauge[nominalTrackGauge] << _id
}
// Sammle ID für RailwayType(s)
railwayTypes.each { railwayType ->
	collect.railwayType[railwayType] << _id
}
// Sammle ID für RailwayUse(s)
railwayUses.each { railwayUse ->
	collect.railwayUse[railwayUse] << _id
}
// Sammle ID für NumberOfTracks
if (numberOfTracks) {
	collect.numberOfTracks[numberOfTracks] << _id
}
// Sammle ID für RailwayElectrification
collect.electrified[electrified] << _id
// Sammle ID für ConditionOfFacility
collect.conditionOfFacility[conditionOfFacility] << _id
// Sammle ID für VerticalPosition
collect.verticalPosition[verticalPosition] << _id

